import React from "react";
import { motion } from "framer-motion";

export default function CentralPointPartnersWebsite() {
  return (
    <div className="min-h-screen bg-gradient-to-b from-slate-50 via-blue-50 to-purple-50 text-slate-700 font-serif overflow-x-hidden">
      {/* Animated Watercolor background elements */}
      <div className="fixed inset-0 opacity-20 pointer-events-none">
        {/* ... same animated background elements ... */}
      </div>

      <header className="relative h-20 p-6 flex justify-between items-center bg-white/80 backdrop-blur-sm shadow-lg sticky top-0 z-50 border-b border-slate-200/50">
        <motion.h1 
          className="text-3xl font-light tracking-wide text-slate-600 italic"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}>
          Central Point Partners
        </motion.h1>
        <nav className="space-x-8">
          {["About", "Services", "Contact"].map((item, i) => (
            <motion.a 
              key={item}
              href={`#${item.toLowerCase()}`} 
              className="text-slate-500 hover:text-slate-700 text-lg font-light tracking-wide transition-all duration-300 hover:underline decoration-2 decoration-blue-300 underline-offset-4"
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: i * 0.1 + 0.3 }}>
              {item}
            </motion.a>
          ))}
        </nav>
      </header>

      <section className="relative flex flex-col items-center justify-center text-center min-h-[120vh] px-8 pt-32 pb-20 overflow-hidden scroll-mt-24" id="hero">
        {/* ... existing animated mountain layers and hero content ... */}
      </section>

      <section id="about" className="scroll-mt-24 relative py-28 px-8 bg-gradient-to-br from-white/80 to-blue-50/80 backdrop-blur-sm">
        {/* ... about content ... */}
      </section>

      <section id="services" className="scroll-mt-24 py-28 px-8 bg-gradient-to-br from-purple-50/60 to-pink-50/60">
        <h3 className="text-4xl font-light text-center mb-12 text-slate-600 italic">
          Pathways to Growth
        </h3>
        <div className="grid md:grid-cols-3 gap-x-12 gap-y-20 max-w-6xl mx-auto">
          {/* ... service cards ... */}
        </div>
      </section>

      <section id="contact" className="scroll-mt-24 py-28 px-8 bg-gradient-to-br from-slate-50 to-blue-50 relative overflow-hidden">
        {/* ... contact form and mountain backdrop ... */}
        <div className="space-y-8 bg-white/70 backdrop-blur-sm p-10 rounded-3xl shadow-lg border border-slate-200/30">
          <input className="w-full px-6 py-4 text-lg border border-slate-200 rounded-2xl bg-white/80 placeholder:text-slate-400 font-light focus:border-slate-400 focus:outline-none focus:ring-2 focus:ring-slate-200 transition-all" type="text" placeholder="Your name" />
          <input className="w-full px-6 py-4 text-lg border border-slate-200 rounded-2xl bg-white/80 placeholder:text-slate-400 font-light focus:border-slate-400 focus:outline-none focus:ring-2 focus:ring-slate-200 transition-all" type="email" placeholder="Your email" />
          <textarea className="w-full px-6 py-4 text-lg border border-slate-200 rounded-2xl bg-white/80 placeholder:text-slate-400 font-light focus:border-slate-400 focus:outline-none focus:ring-2 focus:ring-slate-200 transition-all resize-none" placeholder="Tell us about your journey..." rows="5"></textarea>
          <motion.button className="w-full text-lg px-8 py-4 bg-slate-500/80 text-white rounded-2xl hover:bg-slate-600 transition-all duration-300 font-light tracking-wide shadow-lg backdrop-blur-sm" whileHover={{ y: -2 }} whileTap={{ scale: 0.98 }}>
            Send Message
          </motion.button>
        </div>
      </section>

      <footer className="text-center py-8 text-slate-400 bg-white/60 backdrop-blur-sm border-t border-slate-200/30">
        <div className="font-light">
          Â© {new Date().getFullYear()} Central Point Partners. Nurturing transformation with gentle wisdom.
        </div>
      </footer>
    </div>
  );
}
